import {useState, useEffect} from 'react';
import classes from './ParagraphSections.module.css';
import {BsCaretDownFill, BsFillCircleFill} from 'react-icons/bs';

import OneOption from './OneOption';


import {deleteaquestion} from '../../../../../../CommonApps/AllAPICalls';


import OutsideAlerter from '../../../../../../CommonApps/OutsideAlerter';


const OneQuestion=(props)=>{



    const [showDropDown, setShowDropDown] = useState(false);


    const showDropDownHandler=()=>{

       setShowDropDown(showDropDown=>!showDropDown);

    }

    const deleteQuestionHandler=()=>{

      let questionId = props.question.id;
      deleteaquestion({questionId, props});	    

    }




     console.log("one question: ", props.question.anoptions);


          return   <div className={classes.oneQuestionNoGroup} >
                                   <span className={classes.oneQuestionText} > 
		                       {props.question.questionNum+". "}{props.question.questionText}
		                   </span>
		                   {/*
		                   <OutsideAlerter setDropDown={setShowDropDown} >
				   */}
		                   <div className={classes.dropdownParentQnOption} style={{display:"inline"}}>
                                   <button type="button" className={classes.oneQuestionOptionsButton} onClick={showDropDownHandler}>
		                            <BsCaretDownFill /> 
		                   </button>

                                   { showDropDown &&



			           <div className={classes.dropdownAnswerOptions}>


                                      { props.question.anoptions.map((option, index)=>{


                                           return <OneOption key={index} option={option}/>

				           })

				      }


                                   </div>

                                   }
	                          </div>
		                  {/*
	                          </OutsideAlerter>	
                                  */}
	                          <button type="button" style={{marginLeft:"20px", cursor: "pointer"}} onClick={deleteQuestionHandler}> 
		                      Delete 
		                  </button>

                              </div>


}

export default OneQuestion;
