import {useState, useEffect} from 'react';
import classes from './ParagraphSections.module.css';
import Countdown from 'react-countdown';

import {getallparagraphquestions,  addreadingtaskresponse} from '../CommonApps/AllAPICalls';

import OneQuestion from './OneQuestion';
import OneQuestionGrouped from './OneQuestionGrouped';

import {BsCaretDownFill} from 'react-icons/bs';




const OneParagraphSection=(props)=>{


    let paraId = props.oneParagraph.id;
    let resId = props.responseId;

    console.log("paraId: ", paraId);	

    const initialFormData = Object.freeze({

        readingTaskId: props.oneParagraph !==null ? props.oneParagraph.id : "",
        responseId:  props.responseId !==null ? props.responseId: "",
        });

    const [formData, updateFormData] = useState(initialFormData);


    const [selectedQuestions, setSelectedQuestions] = useState([]);


    const [showQuestionAddForm, setShowQuestionAddForm] = useState(false);

    //onClick={addQuestionHandler(paragraphId)

    const addQuestionHandler=(paragraphId)=>{
      setShowQuestionAddForm(true);
    }

    const [reloadQuestions, setReloadQuestions] = useState(false);

    const closeFormHandler=()=>{
      setShowQuestionAddForm(false);
      setReloadQuestions(reloadQuestions=>!reloadQuestions);
    }

    const [questionsData, getQuestionsData] = useState(null);

    useEffect(()=>{

       let paragraphId = props.oneParagraph.id;	   
       getallparagraphquestions({paragraphId, getQuestionsData});

      return ()=>{

      setSelectedQuestions(selectedQuestions=>[]);
      }

    },[props.oneParagraph, reloadQuestions]);


    const reloadHandler=()=>{

      setReloadQuestions(reloadQuestions=>!reloadQuestions);

    }


    const [showGroupQuestionsForm, setShowGroupQuestionsForm] = useState(false);

    const groupQuestionsHandler=()=>{

       setShowGroupQuestionsForm(true);

    }	


    const closeGroupQuestionFormHandler=()=>{
      setShowGroupQuestionsForm(false);
    }

    const nextPageHandler = () => {
      //props.nextSecHandler();	  
      console.log("posting selected answers");

      console.log("Task Id: ", formData.readingTaskId);
      let responseId = formData.responseId;
      addreadingtaskresponse({paraId, resId, selectedQuestions, props});



    };


    const renderer = ({ hours, minutes, seconds, completed }) => {
      if (completed) {
         // Render a completed state

         return <div> Over </div>

       } else {
       // Render a countdown
	if (minutes ===0){
              return <span style={{ color:"red",paddingLeft:"10px", paddingRight:"10px"}}> <b>{minutes}:{seconds}</b> mins</span>;
         } else{

              return <span> {minutes}:{seconds} mins</span>;
	 }
     }
   };





   let reTime = Number(props.oneParagraph.duration)*60*1000;

   let ttt = Date.now() + reTime;


   const pushSelectedAnswer =(one_Question)=>{


     for (var i = 0; i < selectedQuestions.length; i++) { 

             if( Number(selectedQuestions[i].questionId) === Number(one_Question.questionId) ){
                     selectedQuestions.splice(i,1); 
             };

     }

     setSelectedQuestions(selectedQuestions => [...selectedQuestions, one_Question]);   

   }



   let isSwapped = {value1: props.oneParagraph.flipQnsec , value2: props.oneParagraph.flipQnsec=== 'yes' ? 'no' :'yes' };	

   //const [isSwapped, setIsSwapped] = useState({value1: props.oneParagraph.flipQnsec , value2: props.oneParagraph.flipQnsec=== 'yes' ? 'no' :'yes' });	


    console.log("component rerendering count down ", props.oneParagraph.id); 




return (


<div  className={classes.oneParaSection}>




                  <div className={classes.paraTitle}>
                        <span> <b>{props.oneParagraph.title} </b></span>
	                <div>
                           <span>  Time remaining:  

	                    <Countdown date={ttt}
	                     renderer={renderer}
	                     onComplete={nextPageHandler}
	                     key={2}
	                    />
	                   </span>
	                   <button type="button" className={classes.nextSectionButton} onClick={nextPageHandler}> NEXT </button>
	                </div>
                  </div>
                  <div className={classes.content}>
                        <div className={classes.paragraphTextSec}>

                            { props.oneParagraph.contentType==="image" &&
	                      <img className={classes.userImage} src={props.oneParagraph.paragraphFile} alt='edr Logo' />
			    }

	                    { props.oneParagraph.contentType==="text" &&
                              <div className={classes.paragraphTextSec_margin}>  {props.oneParagraph.paragraphText} </div>
                            }

                        </div>

                        <div className={classes.paragraphQuestionSec}>


                           <div className={classes.genQuestionInfo}> Using the drop-down menu, 
	                            choose the best option according to the information given in the message. 
	                   </div>


                           <div className={classes.allQuestionsOneSec}>

                            { questionsData !==null && questionsData.map((question, index)=>{

                              if(question.grouped=== isSwapped.value1){
                                      return undefined;

                                  }

                                  if(question.grouped===isSwapped.value2){ 

                                         if(isSwapped.value2==="no"){
                                          return <OneQuestion key={index}
						              question={question}
                                                              reload={reloadHandler}
                                                              paragraphId={props.oneParagraph.id}
                                                              oneExamData={props.oneExamData}
                                                              pushSelectedAnswer={pushSelectedAnswer}
                                                              />
                                              }else{

                                           return <OneQuestionGrouped key={index}
						                      question={question}
                                                                      reload={reloadHandler}
                                                                      paragraphId={props.oneParagraph.id}
                                                                      oneExamData={props.oneExamData}
                                                                      pushSelectedAnswer={pushSelectedAnswer}
                                                                      />

                                              }


                                  } 


               			    })

			    }

	                   </div>

                           <div className={classes.allQuestionsOneSec}>


                            { questionsData !==null && questionsData.map((question, index)=>{


                              if(question.grouped=== isSwapped.value2){
                                      return undefined;

                                  }

                                  if(question.grouped===isSwapped.value1){

                                          if(isSwapped.value2==="yes"){
                                          return <OneQuestion key={index}
						              question={question}
                                                              reload={reloadHandler}
                                                              paragraphId={props.oneParagraph.id}
                                                              oneExamData={props.oneExamData}
                                                              pushSelectedAnswer={pushSelectedAnswer}
                                                             />
                                              }else{

                                           return <OneQuestionGrouped key={index}
						                      question={question}
                                                                      reload={reloadHandler}
                                                                      paragraphId={props.oneParagraph.id}
                                                                      oneExamData={props.oneExamData}
                                                                      pushSelectedAnswer={pushSelectedAnswer}
                                                                      />

                                              }


                                  }

                              })



                            }

                           </div>


                        </div>
                  </div>
                  </div>



);






}

export default OneParagraphSection;
