import {useState} from 'react';
import classes from './CreateExamForm.module.css';
import {AiFillCloseCircle} from "react-icons/ai";
import {OptionField, TextInput, TextAreaInput, TextAreaInputSmall} from '../../../../../../../CommonApps/FormInputObjects';

import {addaparagraphsection, addquestiontolisteningtask} from '../../../../../../../CommonApps/AllAPICalls';

import {BsTrash} from 'react-icons/bs'



const AddQuestionListeningTask=(props)=>{


    const initialFormData = Object.freeze({

        questionNum: "",
        questionText: "",
	listeningTaskId: props.listeningTaskId !==null ? props.listeningTaskId : null,
	answerOptions:{"option0":"first option"},
	duration:"0.5"
        });

    const [formData, updateFormData] = useState(initialFormData);

    const handleSubmit=(e)=>{

        e.preventDefault();	   
      if(formData.questionNum === "" && !isNumeric(formData.questionNum)){
            alert('please enter a question number ');
                        return null;
          }

      if(formData.questionText === ""){
            alert('please fill question text');
                        return null;
          }


      if(formData.listeningTaskId ===null){
            alert('No Paragraph Id selected');
                        return null;
          }

      addquestiontolisteningtask({formData, props});

    }


    console.log("formData: ", formData);



    const isNumeric=(str)=> {
       if (typeof str != "string") return false // we only process strings!  
          return !isNaN(str) && // use type coercion to parse the _entirety_ of the string (`parseFloat` alone does not do this)...
          !isNaN(parseFloat(str)) // ...and ensure strings of whitespace fail
    }



    const handleChange=(e)=>{

         if (e.target.name==="questionNum" && !isNumeric(e.target.value)){
	   alert("Please enter only digit");
	   return null;
         }

         updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value,
                });
         }





    const testChangeHandler=(e)=>{

       console.log("name: ", e.target.name, e.target.value);

       let name_=e.target.name;
       let newObj = { ...formData.answerOptions };
       newObj[name_]=e.target.value;	   
       updateFormData({
                        ...formData,
                        ['answerOptions']: newObj,
                  }); 



    }




    const addOneMoreOptionHandler1=()=>{

      let length_= Object.keys(formData.answerOptions).length;	   
      let Key_="option"+ length_;
      let Val_="";
      let answerOptions_="answerOptions";

      formData.answerOptions[Key_] = Val_;

      updateFormData( {
                        ...formData,
                        [answerOptions_]: formData.answerOptions,
                  });

    }




    const deleteOptionHandler=(name_)=>{

     console.log("optionname: ", name_);

     let newObj = { ...formData.answerOptions };
     delete newObj[name_];

     updateFormData({...formData, ['answerOptions']: newObj});

   }










return (

 <div className={classes.createExamFormParent}>


   <form className={classes.createExamForm} onSubmit={handleSubmit} style={{width:"1000px" }}>


   <div className={classes.closeButtonDiv}>
                 <button onClick={props.onPress}
                       className={classes.closeFormButton}>
                            <AiFillCloseCircle className={classes.closeButtonIcon}/>
                 </button>
          </div>





    <div className={classes.inputBoxesDiv}>

       <TextInput handleChange={handleChange}
                  label="Question Number (only digit)"
                  name="questionNum"
                  placeholder="1"
                  requirement="*"
                  />


       <TextAreaInputSmall handleChange={handleChange} 
	          label="Question Text" 
	          name="questionText" 
	          placeholder="Maria’s mother is now"
	          requirement="*"
	          />


        <button type="button" className={classes.addOptionButton} onClick={addOneMoreOptionHandler1}> +Add an answer option </button>




        { Object.keys(formData.answerOptions).map((key, index)=>{

	    //let value_=obj["option"+index];
	    let name_="option"+index;

            return  <div key={index} className={classes.optionsDiv}><input className={classes.oneQuestionAnswerOption}
                       type="text"
                       name={"option"+index}
                       value={formData.answerOptions[name_]}
                       onChange={testChangeHandler}


                        />

		    <button type="button" className={classes.optionDelButton} onClick={()=>deleteOptionHandler(name_)}> <BsTrash/> </button>	
                    </div>

                  }
         	)

	}








	{/*
       <TextAreaInput  handleChange={handleChange}
                  label="Paragraph Text Area"
                  name="paragraphText"
                  placeholder="Dear Maria, The purpose of writing today is to ... "
                  requirement="*"
                  />



       <TextInput handleChange={handleChange} 
	          label="Institute name" 
	          name="institutename" 
	          placeholder="e.g NewCrown English Classes "
	          requirement="*"
	          />

        <OptionField handleChange={handleChange}
                             label="Difficulty level"
                             name="difficultylevel"
                             options={intervals}
	                     requirement="*"
                             />

       */}




             <div className={classes.submitButtonDiv}>
                <button type="submit"  className= {classes.submit_button} ><b> Create question</b> </button>
             </div>


    </div>	

  </form>


 </div>

);


}


export default AddQuestionListeningTask;
