import classes from './ExamResponses.module.css';
import {BsTrash} from 'react-icons/bs';
import {deleteexamresponse} from '../../../../../../CommonApps/AllAPICalls';


import basewebURL from '../../../../../../basewebURL';


const OneResponseShort=(props)=>{



   const deleteResponseHandler=()=>{

   let responseId = props.OneResponse.id;
   deleteexamresponse({responseId, props});	   

   }

   let testurl = basewebURL+"/takethistest"+"/"+props.examId+"/"+props.OneResponse.id;

   const copyLink=()=>{
      navigator.clipboard.writeText(testurl);

   }

   let responseId = props.OneResponse.id;

   const [linkCopied, setLinkCopied]=useState(false);
   const delay = ms => new Promise(res => setTimeout(res, ms));

   const copyTestLinkHandler=async()=>{

        navigator.clipboard.writeText(testurl);
        setLinkCopied(true);
        await delay(3000);
        setLinkCopied(false);

    }








    return <div className={classes.oneExamResponseShort}>

	     <div className={classes.leftBlockResponseShort}>	
               <div className={classes.nameOfCandidate}> Name of candidate: <b> {props.OneResponse.testTakerName} </b> </div>
               <div className={classes.pinCode}>
		 Test Site: 
		 <span style={{color:"blue", marginRight:"20px"}}>  {testurl}
	               <button type="button" style={{marginLeft:"10px"}} onClick={copyTestLinkHandler}> 
		          { linkCopied ? "copied":"copy"}
		       </button>
		 </span>
		 <span> Exam Access Pin: <b> {props.OneResponse.password} </b> </span>
	       </div> 		

	     </div>


            <div className={classes.rightBlockResponseShort}>
                <button  type="button" className={classes.responseDetailViewButton} onClick={()=>props.selectResponse(responseId)}> View Test Result </button>
		<button type="button" className={classes.deleteResponseButton} onClick={deleteResponseHandler}> <BsTrash/> </button>

            </div>

          </div>

}

export default OneResponseShort;
