import {useState,useEffect} from 'react';
import classes from './ParagraphSections.module.css';
import OneListeningQuestion from './OneListeningQuestion';    





const DummyAllListeningQuestions=(props)=>{



    const [questionNo, setQuestionNo] = useState(0);	

    const [oneQuestion, setOneQuestion] = useState(
    	   props.questionsData !==null && props.questionsData.length > 0 ? props.questionsData[questionNo] : null
     );


    console.log("questionsData  ", props.questionsData);


    const nextSecHandler=()=>{

     let nextQnNum=Number(questionNo+1);


      if( props.questionsData !==null  &&
                props.questionsData.length > 0 &&
                 nextQnNum < Number(props.questionsData.length)
          ){

           setOneQuestion(oneQuestion=> props.questionsData[questionNo+1]);

           setQuestionNo(questionNo=>questionNo+1);
        }


      if(props.questionsData !==null  &&
                props.questionsData.length > 0 &&
                nextQnNum  >= Number(props.questionsData.length)){
                props.nextSecHandler();
        }


    }



    console.log("oneQuestion: ", oneQuestion);	

    if (oneQuestion !==null){
    return <OneListeningQuestion OneTask={props.OneTask} 
	                         nextSecHandler={nextSecHandler} 
	                         oneQuestion={oneQuestion}
	                         listenSubSectionNo={props.listenSubSectionNo}
	                         pushSelectedAnswer={props.pushSelectedAnswer}
		                 />

    }else{

    return <div>

	No Questions.listening section	    

	</div>	    

    }




}


export default DummyAllListeningQuestions;
