import {useState} from 'react'; 
import classes from './WritingTest.module.css';

import AddWritingTaskForm from './Forms/AddWritingTaskForm';


import OneWritingTask from './OneWritingTask';

const WritingTest=(props)=>{


  const [showWritingTaskForm, setShowWritingTaskForm] = useState(false);






  const showAddWritingTaskFormHandler=()=>{
      setShowWritingTaskForm(true);

  }


  const closeWRTFormHandler=()=>{
     setShowWritingTaskForm(false);
     props.rerender();
  }




  console.log("One Exam Data: ", props.oneExamData);



return <div className={classes.writingTest}>

   <div className={classes.buttonsSection}>		
       <button type="button" className={classes.writingTaskButton} onClick={showAddWritingTaskFormHandler}> +Add Writing Task</button> 
   </div>

   { showWritingTaskForm &&

            <AddWritingTaskForm onPress={closeWRTFormHandler}
	                        oneExamData={props.oneExamData}
	                        />
   }






  { props.oneExamData !==null && props.oneExamData.writingTest !==null &&  props.oneExamData.writingTest.writingSections !==null  && 


     props.oneExamData.writingTest.writingSections.map((OneTask, index)=>{


       return <OneWritingTask key={index}  OneTask={OneTask} rerender={props.rerender}/>

       })

  }		  

</div>


}


export default WritingTest;
