import {useState} from 'react';
import classes from './GroupQuestionsForm.module.css';
import {AiFillCloseCircle} from "react-icons/ai";


import CheckBoxInputs from './CheckBoxInputs';


import {groupquestionstoonesec} from '../../../../../../../CommonApps/AllAPICalls';


const GroupQuestionsForm=(props)=>{


   const [checkedCourses,setCheckedCourses]=useState([]);

   const [selectedQuestionIds, setSelectedQuestionIds] = useState([]);


   const handleSubmit=(e)=>{
     e.preventDefault();

     let sel_questionIds =[]	   

     { checkedCourses.length > 0 && Number(checkedCourses.length) === Number(props.questionsData.length) &&	   
      props.questionsData.forEach((question, index)=>{

	checkedCourses[index] &&     
        sel_questionIds.push(question.id)

         })


     }

     let paragraphId = props.paragraphId;	   
     groupquestionstoonesec({paragraphId, sel_questionIds, props});	   

     console.log("sel_questionIds: ", sel_questionIds)	   


   }




   console.log("checked Courses: ", checkedCourses);




return <div className={classes.groupQuestionsParent}>


       <form className={classes.groupQuestionsForm} onSubmit={handleSubmit}>

        <div className={classes.closeButtonDiv}>
                 <button onClick={props.onPress}
                       className={classes.closeFormButton}>
                            <AiFillCloseCircle className={classes.closeButtonIcon}/>
                 </button>
          </div>



         <CheckBoxInputs
             label="Select the questions to be grouped in a paragraph"
             placeholder="jai"
             name="postCourses"
             Courses={props.questionsData}
             setCheckedCourses={setCheckedCourses}
	     setSelectedQuestionIds={setSelectedQuestionIds}
             />


         <div className={classes.submitButtonDiv}>
             <button type="submit"  className= {classes.submit_button} ><b> Group these into one section</b> </button>
         </div>



       </form>

    </div>


}


export default GroupQuestionsForm;
