import {useState, useEffect} from 'react';
import classes from './ParagraphSections.module.css';

import AddQuestionSection from './Forms/AddQuestionListeningTask';

import {getlisteningtaskquestions, deletealisteningtask, speakallsections} from '../../../../../../CommonApps/AllAPICalls';

import OneQuestion from './OneQuestion';
import {BsCaretDownFill} from 'react-icons/bs';


import GroupQuestionsForm from './Forms/GroupQuestionsForm';

import ReactPlayer from 'react-player';




const OneListeningTask=(props)=>{



    const [showQuestionAddForm, setShowQuestionAddForm] = useState(false);

    //onClick={addQuestionHandler(paragraphId)

    const addQuestionHandler=(paragraphId)=>{
      setShowQuestionAddForm(true);
    }

    const [reloadQuestions, setReloadQuestions] = useState(false);

    const closeFormHandler=()=>{
      setShowQuestionAddForm(false);
      setReloadQuestions(reloadQuestions=>!reloadQuestions);
    }

    const [questionsData, getQuestionsData] = useState(null);

    useEffect(()=>{

       let listeningTaskId = props.OneTask.id;	   
       getlisteningtaskquestions({listeningTaskId, getQuestionsData});

    },[props.OneTask, reloadQuestions]);



   console.log("questionsData: ", questionsData);

   const deleteParagraphHandler=()=>{
       let listeningTaskId=props.OneTask.id;
       deletealisteningtask({listeningTaskId, props});
   }


   const reloadHandler=()=>{

    setReloadQuestions(reloadQuestions=>!reloadQuestions);

   }


   const [showGroupQuestionsForm, setShowGroupQuestionsForm] = useState(false);

   const groupQuestionsHandler=()=>{

     setShowGroupQuestionsForm(true);

   }	


   const closeGroupQuestionFormHandler=()=>{

   setShowGroupQuestionsForm(false);
   }

   let VarSpeak= String(props.OneTask.speakQuestions);
   const [checked, setChecked] = useState( props.OneTask.speakQuestions === "yes"? true : false);


   const speakCheckHandler=()=>{

   //setChecked(checked=>!checked);
   let listeningTaskId = props.OneTask.id;
   let speakdecision = checked === "yes"? "no": "yes"	   
   speakallsections({listeningTaskId, speakdecision, setChecked});

   }


   console.log("OneTask Listening:  ", props.OneTask);



return (


<div  className={classes.oneParaSection}>


                { showQuestionAddForm &&

                   <AddQuestionSection onPress = {closeFormHandler}
                                 listeningTaskId={props.OneTask.id}
                               />

                }


                { showGroupQuestionsForm &&


                   <GroupQuestionsForm onPress={closeGroupQuestionFormHandler}
			               questionsData={questionsData}
				       />



		}




                  <div className={classes.paraTitle}>
                        <span> <b>{props.OneTask.title} </b></span>
	                <button type="button" onClick={deleteParagraphHandler} style={{cursor:"pointer"}}> Delete </button>
                        <span> Preparation Time : {props.OneTask.preparation_time} mins </span>
                  </div>
                  <div className={classes.content}>
                        <div className={classes.paragraphTextSec}>


                            <div className={classes.paragraphTextSec_margin}>  {props.OneTask.instructionText} </div>

                            { props.OneTask.instructionImage !==null &&
	                       <img className={classes.listeningImage} src={props.OneTask.instructionImage} alt='edr Logo' />
			    }


	                    <div> Audio/Video File </div>
	                    <div className={classes.audioFilePlay}>
	                    <ReactPlayer url={props.OneTask.audioFile} width="100%" height="100%" controls={true} />
                            </div>  

                        </div>

                        <div className={classes.paragraphQuestionSec}>

                        <div className={classes.buttonAndToggleBox}>
	                  <button type="button" className={classes.addQuestionButton} onClick={addQuestionHandler}> +Add a question </button>

	                  <div className={classes.speakButtonsParent}>
	                    <input type="checkbox" 
	                           value = {checked} 
	                           className={classes.checkBoxSpeak} 
	                           onClick={speakCheckHandler}
	                           />

                            {checked  && <span> Speak all questions </span>}
	                    {!checked && <span> Do not speak questions </span>}

	                  </div>                  

                        </div>

                           <div className={classes.allQuestionsOneSec}>


                            { questionsData !==null && questionsData.map((question, index)=>{


                             return <OneQuestion key={index}
				                 question={question}
				                 reload={reloadHandler}
					    />


			    })

			    }


	                   </div>

                        </div>
                  </div>
                  </div>








);






}

export default OneListeningTask;
