import {useState} from 'react';
import classes from './ParagraphSections.module.css';



import OneListeningTask from './OneListeningTask';

import InfoHeader from './InfoHeader';


const ListeningTest=(props)=>{

    const [listeningSecNo, setListeningSecNo] = useState(0);

    const [ ListeningTask, setOneListeningTask] = useState(
             props.oneExamData.listeningTest !==null  && props.oneExamData.listeningTest.listeningSections.length > 0 ?
             props.oneExamData.listeningTest.listeningSections[listeningSecNo] : null
    );


    const nextSecHandler=()=>{


         let nextListenNum=Number(listeningSecNo+1);

        if(props.oneExamData.listeningTest !==null  &&
                props.oneExamData.listeningTest.listeningSections.length > 0 &&
                nextListenNum < Number(props.oneExamData.listeningTest.listeningSections.length)
          ){ 

           setOneListeningTask(ListeningTask=> props.oneExamData.listeningTest.listeningSections[listeningSecNo+1]);
           setListeningSecNo(listeningSecNo=>listeningSecNo+1);
        }


        if(props.oneExamData.listeningTest !==null  &&
                props.oneExamData.listeningTest.listeningSections.length > 0 &&
                nextListenNum >= Number(props.oneExamData.listeningTest.listeningSections.length)){

                props.next();

        }


       if(props.oneExamData.listeningTest !==null  && props.oneExamData.listeningTest.listeningSections.length === 0 ){

         props.next();

        }





    }








  const closeFormHandler=()=>{

  }



    console.log("One Exam Data: ", props.oneExamData);

   return (

      <div className={classes.paragraphSections} >


       <InfoHeader info="You are now taking Listening Test"/>



         {/* props.oneExamData !==null && 
	            props.oneExamData.listeningTest !==null && 
	            props.oneExamData.listeningTest.listeningSections.map((OneTask,index)=>{

                    return <OneListeningTask OneTask={OneTask} rerender={closeFormHandler}/>


             })

         */}


        { ListeningTask !==null &&

          <OneListeningTask OneTask={ListeningTask} 
		            rerender={closeFormHandler} 
		            nextSecHandler={nextSecHandler}
		            responseId={props.responseId}
			    /> 
	}


         { ListeningTask ===null &&
              <>
              <div> No material in this section </div>
              <button type="button" onClick={nextSecHandler} style={{ color:"white", backgroundColor:"blue",marginTop:"10px",cursor:"pointer"}}> Move to Next </button>
             </>
           }








  </div>

);


}

export default ListeningTest;
