import {useState} from 'react'; 
import classes from './WritingTest.module.css';
import AddSpeakingTaskForm from './Forms/AddSpeakingTaskForm';
import OneSpeakingTask from './OneSpeakingTask';




const SpeakingTest=(props)=>{


   const [showSpeakingTaskForm, setShowSpeakingTaskForm] = useState(false);

   const showAddSpeakingTaskFormHandler=()=>{
      setShowSpeakingTaskForm(true);

   }

   const closeWRTFormHandler=()=>{
     setShowSpeakingTaskForm(false);
     props.rerender();
   } 


   console.log("One Exam Data: ", props.oneExamData);



return <div className={classes.writingTest}>

     <div className={classes.buttonsSection}>		
         <button type="button" className={classes.writingTaskButton} onClick={showAddSpeakingTaskFormHandler}> +Add Speaking Task</button> 
     </div>

     { showSpeakingTaskForm &&

            <AddSpeakingTaskForm onPress={closeWRTFormHandler}
	                        oneExamData={props.oneExamData}
	                        />
     }



    { props.oneExamData !==null && props.oneExamData.speakingTest !==null &&  props.oneExamData.speakingTest.speakingSections !==null  && 


        props.oneExamData.speakingTest.speakingSections.map((OneTask, index)=>{

             return <OneSpeakingTask key={index}  OneTask={OneTask} rerender={props.rerender}/>

        })

    }		  

</div>


}


export default SpeakingTest;
