import {useState, useEffect} from 'react';
import classes from './ParagraphSections.module.css';

import {getlisteningtaskquestions } from '../CommonApps/AllAPICalls';

import DummyAllListeningQuestions from './DummyAllListeningQuestions';
import DummyAllListeningQuestionsNoSpeak from './DummyAllListeningQuestionsNoSpeak';





const AllQuestionsListeningSection=(props)=>{



     const [questionsData, getQuestionsData] = useState(null);

      useEffect(()=>{

       let listeningTaskId = props.OneTask.id;
       getlisteningtaskquestions({listeningTaskId, getQuestionsData});

       return ()=>{

       getQuestionsData(questionsData=>null);
       }

      },[props.OneTask.id ])











    //return <OneListeningQuestion OneTask={props.OneTask} nextSecHandler={nextSecHandler} oneQuestion={oneQuestion}/>       
     return  <>
               { props.OneTask !==null && questionsData !==null && questionsData.length > 0  &&  props.OneTask.speakQuestions === "yes" &&
		<DummyAllListeningQuestions 
		                          questionsData={questionsData} 
		                          OneTask={props.OneTask}
		                          nextSecHandler={props.nextSecHandler}
		                          listenSubSectionNo={props.listenSubSectionNo}
		                          pushSelectedAnswer={props.pushSelectedAnswer}
		                          />
               }


               { props.OneTask !==null && questionsData !==null &&  questionsData.length > 0  && props.OneTask.speakQuestions === "no" &&

               <DummyAllListeningQuestionsNoSpeak  OneTask={props.OneTask}
		                                   questionsData={questionsData}
		                                   nextSecHandler={props.nextSecHandler}
		                                   listenSubSectionNo={props.listenSubSectionNo}
		                                   pushSelectedAnswer={props.pushSelectedAnswer}
			                           />


	       }


	       { props.OneTask !==null && questionsData !==null && questionsData.length === 0 && 


	          <div> Next Section </div>		       

	       }




              </>

}


export default AllQuestionsListeningSection;
