import {useState,useEffect, useRef} from 'react';
import classes from './WritingTest.module.css';
import {TextAreaInput} from '../CommonApps/FormInputObjects';
import Countdown from 'react-countdown';
import {BsStopwatch} from 'react-icons/bs';

import {FaMicrophoneAlt} from 'react-icons/fa';

import {addspeakingtaskresponse} from '../CommonApps/AllAPICalls';

//import { createFFmpeg, fetchFile } from '@ffmpeg/ffmpeg';

//import { fetchFile } from '@ffmpeg/ffmpeg';


const OneSpeakingTask=(props)=>{






   const [selectedAudioFile, setSelectedAudioFile] = useState(null);

   const [mediaRecorder, setMediaRecorder] = useState(null);
   const [audioChunks, setAudioChunks] = useState([]);
   const startRecording = () => {
   navigator.mediaDevices.getUserMedia({ audio: true })
    .then(stream => {
      const recorder = new MediaRecorder(stream);
      setMediaRecorder(recorder);
      const chunks = [];

      recorder.addEventListener("dataavailable", event => {
        chunks.push(event.data);
      });

      recorder.addEventListener("stop", () => {
        const audioBlob = new Blob(chunks, { type: 'audio/mpeg-3' });

	console.log("audoBlob", audioBlob);
	let responseId = props.responseId;
        let speakingTaskId = props.OneTask.id;
        const formData = new FormData();
        formData.append('audioFile', audioBlob,'audio.mp3');
        //formData.append('audioFile', selectedAudioFile.image[0]);
        formData.append('speakingTaskId',speakingTaskId );
        addspeakingtaskresponse({responseId, formData, props});







        const audioUrl = URL.createObjectURL(audioBlob);
        const downloadLink = document.createElement('a');
        downloadLink.href = audioUrl;
        downloadLink.download = 'audio.mp3';
        document.body.appendChild(downloadLink);

        //console.log("downloaded link: ", downloadLink);
        downloadLink.click();
        document.body.removeChild(downloadLink);
        setAudioChunks([]);
        setMediaRecorder(null);
      });

      recorder.start();
    });
  };

  const handleChangeAudioFile=(e)=>{

       if(e.target && e.target.files[0]){

            if(e.target.name === 'audioFile' ){

                  setSelectedAudioFile({
                        image: e.target.files
                  });

            }

       }

    }








  const stopRecording = () => {
    mediaRecorder.stop();


  };





   const [rerender, setRerender] = useState(false);

    useEffect(()=>{
      //console.log("recording started");
      //startRecording();

   },[rerender]);


   const RerenderHandler=()=>{
      setRerender(rerender=>!rerender);
   }

   console.log("audioChunks:::::::::;: ", audioChunks);


   const handleChange=()=>{

   }

   const renderer = ({ hours, minutes, seconds, completed }) => {
      if (completed) {
       // Render a completed state
         return <div> Over </div>

       } else {
       // Render a countdown
        if (minutes ===0){
              return <span style={{ color:"red",paddingLeft:"10px", paddingRight:"10px"}}> <b> {minutes}:{seconds}</b> mins</span>;
         } else{

              return <span> {minutes}:{seconds} mins </span>;
         }
       }
     };


   let prep_time = Date.now() + Number(props.OneTask !==null ? props.OneTask.preparation_time : 0 )*60*1000;


   let record_time =  Date.now() + Number(props.OneTask !==null ? props.OneTask.recording_time : 0 )*60*1000;


   const [recordTimer, setRecordTimer] = useState(false);	


    useEffect(()=>{
        setRecordTimer(false);
    },[props.OneTask.id]);




    const moveToRecordCountHandler=()=>{
      setRecordTimer(true);
    }


    const nextSecHandler=()=>{


       if(!recordTimer){
           setRecordTimer(true);
       }
       if(recordTimer){
	  //stopRecording();     
	  //props.nextSecHandler();
       }

    }











return  <div className={classes.oneWritingTask}>

              <div className={classes.writingTitle}>
                        <span> <b> {props.OneTask.title} </b></span>
                        <div>
                           <span> Preparartion Time : {props.OneTask.preparation_time} mins </span>
                           <span style={{marginLeft:"20px"}}> Recording Time : {props.OneTask.recording_time} mins </span>
                           <button type="button" className={classes.nextSectionButton} onClick={nextSecHandler}> NEXT </button>
                        </div>


              </div>


              <div className={classes.textQuestionBoxes} >

		  <div className={classes.textQuestionBoxLeft} style={{width: "100%"}}>

		        <div className={classes.audioSpeakerBox}>
                            { !recordTimer && <>
		              <div style={{marginRight:"10px"}}> Preparation time </div>		         
                              <BsStopwatch/>

		              <Countdown date={prep_time}
                                  renderer={renderer}
	                          onComplete={moveToRecordCountHandler}
                                  key={props.OneTask.id}
                                 />
			      </>	   
                            }

		            {  recordTimer &&

	                       <>
			       <div style={{marginRight:"10px"}}> Speak now </div>		

                                <input
                                   type="file"
                                   onChange={handleChangeAudioFile}
                                   name="audioFile"
                                   />



			       <FaMicrophoneAlt/>
                               <Countdown date={record_time}
                                  renderer={renderer}
                                  onComplete={nextSecHandler}
                                  key={props.OneTask.id}
                                 />
			       </>		    
                            }


                        </div>

		         <div style={{color:"var(--themeColor)",marginBottom:"20px",marginTop:"30px"}}><b> {props.OneTask.questionText}</b></div> 

                         { props.OneTask.imageFile !==null && 
	          	         <img className={classes.userImage} src={props.OneTask.imageFile} alt='edr Logo' />
                         }








		  </div>

              </div>


    </div>



}


export default OneSpeakingTask;
