import {useState} from 'react';
import classes from './ParagraphSections.module.css';



import OneParagraphSection from './OneParagraphSection';


import InfoHeader from './InfoHeader';

const ReadingTest=(props)=>{



    const [paraSecNo, setParaSecNo] = useState(0);

    const [OneParagraph, setOneParagraph] = useState(
	    props.oneExamData.readingTest !==null  && props.oneExamData.readingTest.paragraphSections.length > 0 ? 
	     props.oneExamData.readingTest.paragraphSections[paraSecNo] : null
    );


    const nextSecHandler=()=>{


	 let paraNextNum=Number(paraSecNo+1);  

	if(props.oneExamData.readingTest !==null  && 
		props.oneExamData.readingTest.paragraphSections.length > 0 &&  
		 paraNextNum < Number(props.oneExamData.readingTest.paragraphSections.length)
	  ){   

           setOneParagraph(OneParagraph=> props.oneExamData.readingTest.paragraphSections[paraSecNo+1]);
   	   setParaSecNo(paraSecNo=>paraSecNo+1);
        }


        if(props.oneExamData.readingTest !==null  &&
                props.oneExamData.readingTest.paragraphSections.length > 0 &&
                 paraNextNum >= Number(props.oneExamData.readingTest.paragraphSections.length)){

		props.next();

	}





    }




   return (

      <div className={classes.paragraphSections} >


         {/* props.oneExamData !==null && 
	            props.oneExamData.readingTest !==null && 
	            props.oneExamData.readingTest.paragraphSections.map((oneParagraph,index)=>{

                    let paragraphId = oneParagraph.id;

                    return <OneParagraphSection key={index} oneParagraph={oneParagraph}
			                                    rerender={props.rerender}
				                            />


             })

        */}


             <InfoHeader info="You are taking reading test now!"/>
           { OneParagraph !==null && 
	     <OneParagraphSection  oneParagraph={OneParagraph}
                                 rerender={props.rerender}
	                         nextSecHandler={nextSecHandler}
		                 oneExamData={props.oneExamData}
		                 responseId={props.responseId}
                                 />

           }


  </div>

);


}

export default ReadingTest;
