import {useState, useEffect} from 'react';
import classes from './ParagraphSections.module.css';
import {BsInfoCircle} from 'react-icons/bs';
import {GrRadialSelected} from 'react-icons/gr';
//import ReactPlayer from 'react-player';
//<ReactPlayer url={props.OneTask.audioFile} width="100%" height="100%" controls={true} />
import OptionButton from './OptionButton';
import Countdown from 'react-countdown';
import { useSpeechSynthesis } from "react-speech-kit";



const OneListeningQuestion =(props)=>{


    const { speak } = useSpeechSynthesis();

    const [selectedId, setSelectedId] = useState(null);    

    const [optionColor, setOptionColor] = useState({backgroundColor:"white",padding:"5px",fontSize:"16px",borderRadius:"50%"});

    let InActiveColor={backgroundColor:"white",padding:"5px",fontSize:"16px",borderRadius:"50%"};
    let activeColor = {backgroundColor:"grey",padding:"5px",fontSize:"16px",borderRadius:"50%"};

    const selectOptionHandler=(optionId)=>{

     setSelectedId(selectedId=>optionId);

     let one_Question = {"questionId": props.oneQuestion.id,"answerId": optionId}
     props.pushSelectedAnswer(one_Question);	    

    }


     const renderer = ({ hours, minutes, seconds, completed }) => {
      if (completed) {
       // Render a completed state

         return <div> Over </div>

       } else {
       // Render a countdown
        if (minutes ===0){
              return <span style={{ color:"red",paddingLeft:"10px", paddingRight:"10px"}}> <b> {minutes}:{seconds}</b> mins</span>;
         } else{

              return <span> {minutes}:{seconds} mins </span>;
         }
       }
     };


     let reTime = Number(props.oneQuestion !==null ? props.oneQuestion.duration : 0 )*60*200*1000;


     let timeRemain = Date.now() + reTime;

     const speakHandler=()=>{

     }



     useEffect(()=>{

       let qnText= props.oneQuestion.questionText;	  
       speak({ text: qnText });     
       return ()=>{

       }

     },[props.oneQuestion.id, props.OneTask.id, props.oneQuestion.questionText]);

    //props.oneQuestion.id, props.OneTask.id, props.listenSubSectionNo

    const nextSecHandler=()=>{


    props.nextSecHandler();

    }




          return   <>
                       <div className={classes.paraTitle}>
                          <span> <b>{props.OneTask.title} </b></span>
                          <div>
                              <span> Remaining Time :
	                         <Countdown date={timeRemain}
                                  renderer={renderer}
                                  onComplete={props.nextSecHandler}
                                  key={props.oneQuestion.id}
                                 />
	                       </span>

                               <button type="button" className={classes.nextSectionButton} onClick={ nextSecHandler}> NEXT </button>
                          </div>
                      </div>

                       <div className={classes.listeningQuestionSection}>

                          <div className={classes.leftBoxListeningQuestion}>
                             <div style={{color:"var(--themeColor)",marginTop:"2.5%"}}> <b>Listen to the question. You will hear it only once.</b> </div>      
                              <div className={classes.infoBoxListenQuestion}>
                                    <BsInfoCircle className={classes.infoIconListen}/>
                                    <div> Click "NEXT" to continue. </div>
		                    <div> {props.oneQuestion.questionText} </div>
                              </div>


                          </div>

                          <div className={classes.rightBoxListeningQuestion}>

                             <div className={classes.chooseBestAnswerInstruct}> Choose the best answer to each question.  </div>


                             <div className={classes.questionNumInfo}> Question no. <b>{props.oneQuestion !==null && props.oneQuestion.questionNum} </b>  </div>

                             <div className={classes.oneQuestionAnswerOptionDiv}>



                               { props.oneQuestion !==null && props.oneQuestion.anoptions.map(( option, index)=>{

                                   let optionId = option.id;

                                   return <OptionButton option={option} 
				                        key={index} 
				                        selectOptionHandler={()=>selectOptionHandler(optionId)} 
				                        optionColor={ selectedId === optionId ? activeColor: InActiveColor }
					                />

			           })

			       }

		             </div>

                          </div>

                       </div>


                  </>



}

export default OneListeningQuestion;
