import {useState} from 'react';
import classes from './GroupQuestionsForm.module.css';
import {AiFillCloseCircle} from "react-icons/ai";

import {OptionField, TextInput, TextAreaInput, TextAreaInputSmall} from '../../../../../../../CommonApps/FormInputObjects';

import {addawritingtask} from '../../../../../../../CommonApps/AllAPICalls';


const AddWritingTaskForm=(props)=>{


    const initialFormData = Object.freeze({
        title: "",
        duration:"",
        writingTestId: props.oneExamData !==null && props.oneExamData.writingTest !==null  ? props.oneExamData.writingTest.id : null,
        questionText1:"",
	questionText2:""
        });

    const [formData, updateFormData] = useState(initialFormData);

    console.log("initialFormData: ", props.oneExamData);



    const handleChange=(e)=>{

     updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value,
                });





    }


    const handleSubmit=(e)=>{
    e.preventDefault();

    addawritingtask({formData, props});	    

    }


   console.log("formData: ", formData);



return <div className={classes.groupQuestionsParent}>


       <form className={classes.groupQuestionsForm} onSubmit={handleSubmit} style={{width:"80vw",maxWidth:"1200px"}}>

        <div className={classes.closeButtonDiv}>
                 <button onClick={props.onPress}
                       className={classes.closeFormButton}>
                            <AiFillCloseCircle className={classes.closeButtonIcon}/>
                 </button>
        </div>

         <TextInput handleChange={handleChange}
                  label="Writing Task Title"
                  name="title"
                  placeholder="e.g. Practice Test A - Writing Task 1"
                  requirement="*"
                  />


        <TextInput handleChange={handleChange}
                  label="Duration in minutes"
                  name="duration"
                  placeholder="28"
                  requirement="*"
                  />


         <div className={classes.textInfoBoxes}>

           <div className={classes.textInfoBoxLeft}>

              <TextAreaInput  handleChange={handleChange}
                  label="Writing Topic "
                  name="questionText1"
                  placeholder="e.g. You recently made reservations for dinner at a very famous and expensive restaurant in town. However, the meal and the service were terrible. The restaurant manager was not available to solve the problem, so you left without a resolution."
                  requirement="*"
                  />
           </div>


	   <div className={classes.textInfoBoxRight}>
              <TextAreaInput  handleChange={handleChange}
                  label="Writing info and instruction"
                  name="questionText2"
                  placeholder="e.g. Write an email to the restaurant's manager in about 150-200 words. Your email should do the following things:

                    State what problems you had with the food you ordered.
                    Complain about the service.
                    Describe how you want the restaurant to resolve the problem to your satisfaction. "
                  requirement="*"
                  />

           </div>	

         </div>


         <div className={classes.submitButtonDiv}>
             <button type="submit"  className= {classes.submit_button} ><b> Add</b> </button>
         </div>


       </form>

    </div>


}


export default AddWritingTaskForm;
