import {useState, useEffect} from 'react';
import classes from './ParagraphSections.module.css';

//import {getlisteningtaskquestions } from '../CommonApps/AllAPICalls';

import {addlisteningtaskresponse} from '../CommonApps/AllAPICalls';

import OneQuestion from './OneQuestion';
import {BsCaretDownFill} from 'react-icons/bs';
import AllQuestionsListeningSection from './AllQuestionsListeningSection';
import ReactPlayer from 'react-player';
import Countdown from 'react-countdown';




const OneListeningTask=(props)=>{


     const [selectedQuestions, setSelectedQuestions] = useState([]);

     const [listenSubSectionNo, setListenSubSectionNo] = useState(0);        

     const [reloadQuestions, setReloadQuestions] = useState(false);

     //const [questionsData, getQuestionsData] = useState(null);

     useEffect(()=>{

        //let listeningTaskId = props.OneTask.id;	   
        //getlisteningtaskquestions({listeningTaskId, getQuestionsData});
        setListenSubSectionNo(listenSubSectionNo=>0); 

     },[props.OneTask, reloadQuestions, setListenSubSectionNo]);

     //console.log("questionsData: ", questionsData);

     const reloadHandler=()=>{

       setReloadQuestions(reloadQuestions=>!reloadQuestions);

     }


     const nextSecHandler=()=>{

       if(listenSubSectionNo ===2 ){

         let listeningTaskId = props.OneTask.id;
	 let responseId = props.responseId;      

	 addlisteningtaskresponse({listeningTaskId, responseId, selectedQuestions, props});      

         //props.nextSecHandler();



       }

       if(listenSubSectionNo < 2 ){	     
           setListenSubSectionNo(listenSubSectionNo=>listenSubSectionNo+1);
       }


     }



     const renderer = ({ hours, minutes, seconds, completed }) => {
      if (completed) {
       // Render a completed state

         return <div> Over </div>

       } else {
       // Render a countdown
        if (minutes ===0){
              return <span style={{ color:"red",paddingLeft:"10px", paddingRight:"10px"}}><b> {minutes}:{seconds}</b> mins</span>;
         } else{

              return <span> {minutes}:{seconds} minutes</span>;
         }
       }
     };



   let reTime = Number(props.OneTask.preparation_time)*60*1000;


   let timePrep =  Date.now()+reTime;


   //let timeRemain = Date.now() + Number(props.OneTask.preparation_time)*10000;


   console.log("props.OneTask.audioFile: ", props.OneTask.audioFile);

    const pushSelectedAnswer =(one_Question)=>{

     for (var i = 0; i < selectedQuestions.length; i++) {

             if( Number(selectedQuestions[i].questionId) === Number(one_Question.questionId) ){
                     selectedQuestions.splice(i,1);
             };

     }

     setSelectedQuestions(selectedQuestions => [...selectedQuestions, one_Question]);

   }







  console.log("speakQuestions: ", props.OneTask.speakQuestions);




return (


<div  className={classes.oneParaSection}>

                 { listenSubSectionNo ===0 &&

                  <>
                  <div className={classes.paraTitle}>
                        <span> <b>{props.OneTask.title} </b></span>
                        <div>
                           <span> Time remaining :
                            <Countdown date={timePrep}
                             renderer={renderer}
                             onComplete={nextSecHandler}
                             key={props.OneTask.id}
                            />

                           </span>


                           <button type="button" className={classes.nextSectionButton} onClick={ nextSecHandler}> NEXT </button>
                        </div>
                  </div>



                  <div className={classes.oneListeningInstructionSection}> 
                     <div style={{color:"var(--themeColor)"}}> <b>Instructions</b> </div>

                     <div className={classes.textInstructionListeningSection}> {props.OneTask.instructionText}  </div>

                     { props.OneTask.instructionImage !==null &&
                               <img className={classes.listeningInsturctionSecImage} src={props.OneTask.instructionImage} alt='edr Logo' />
                     }


	          </div>


                  </>

                 }


	         { listenSubSectionNo ===1 &&  

                  <>

                  <div className={classes.paraTitle}>
                        <span> <b>{props.OneTask.title} </b></span>
                        <div>
                           <button type="button" className={classes.nextSectionButton} onClick={ nextSecHandler}> NEXT </button>
                        </div>
                  </div>


                  <div className={classes.listeningSecMainAudioVideo}> 

		   <ReactPlayer url={props.OneTask.audioFile} 
			        width="100%" 
			        height="100%" 
			        controls={true}  
			        className={classes.videoAudioBoxLS}
			        playing={true}
			        onEnded={nextSecHandler}
				/>		

                  <div className={classes.blockBox}> </div>

                  </div>




                  </>


                 }


                 {  listenSubSectionNo ===2 &&


                     <AllQuestionsListeningSection  OneTask={props.OneTask} 
			                            nextSecHandler={nextSecHandler}
			                            listenSubSectionNo={listenSubSectionNo}
			                            pushSelectedAnswer={pushSelectedAnswer}
				                    />


		 }




 </div>

);

}

export default OneListeningTask;
