import {useState} from 'react';
import classes from './GroupQuestionsForm.module.css';
import {AiFillCloseCircle} from "react-icons/ai";
import {OptionField, TextInput, TextAreaInput, TextAreaInputMedium} from '../../../../../../../CommonApps/FormInputObjects';
import {addlisteningtaskwimage, addspeakingtaskwoimage} from '../../../../../../../CommonApps/AllAPICalls';




const AddListeningTaskForm=(props)=>{

    const [selectedFile, setSelectedFile] = useState(null);

    const [selectedAudioFile, setSelectedAudioFile] = useState(null);

    const initialFormData = Object.freeze({
        title: "",
        preparation_time:"",
	instructionText:"",
        listeningTestId: props.oneExamData !==null && props.oneExamData.listeningTest !==null  ? props.oneExamData.listeningTest.id : null,
        duration:""       
        });

    const [formData, updateFormData] = useState(initialFormData);


    const handleChange=(e)=>{

     updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value,
                });

    }


    const handleChangeImage=(e)=>{

       if(e.target && e.target.files[0]){

            if(e.target.name === 'instructionImage' ){

                  setSelectedFile({
                        image: e.target.files
                  });

            }

       }

    }


    const handleChangeAudioFile=(e)=>{

       if(e.target && e.target.files[0]){

            if(e.target.name === 'audioFile' ){

                  setSelectedAudioFile({
                        image: e.target.files
                  });

            }

       }

    }




    const handleSubmit=(e)=>{
      e.preventDefault();
      let newFormData = new FormData();
      newFormData.append('title', formData.title);
      newFormData.append('preparation_time', formData.preparation_time);
      newFormData.append('instructionText', formData.instructionText);
      newFormData.append('listeningTestId', formData.listeningTestId);

      {selectedFile !== null &&

      newFormData.append('instructionImage', selectedFile.image[0]);		      
      newFormData.append('audioFile', selectedAudioFile.image[0]);	      
      addlisteningtaskwimage({newFormData, props});
      }

      {/* selectedFile === null &&	      
        addspeakingtaskwoimage({formData, props});	    
      */}

    }


    const removeImageHandler=(e)=>{

      setSelectedFile(selectedFile=>null);

    }


   const removeAudioFileHandler =(e)=>{

     setSelectedAudioFile(selectedAudioFile=>null);
   }



   console.log("formData: ", formData);

   console.log("selectedFile", selectedFile);

   console.log("selectedAudioFile", selectedAudioFile);


return <div className={classes.groupQuestionsParent}>


       <form className={classes.groupQuestionsForm} onSubmit={handleSubmit} style={{width:"80vw",maxWidth:"1200px"}}>

        <div className={classes.closeButtonDiv}>
                 <button onClick={props.onPress}
                       className={classes.closeFormButton}>
                            <AiFillCloseCircle className={classes.closeButtonIcon}/>
                 </button>
        </div>

        <TextInput handleChange={handleChange}
                  label="Listening Task Title"
                  name="title"
                  placeholder="e.g. Practice Test A - Writing Task 1"
                  requirement="*"
                  />


        <TextInput handleChange={handleChange}
                  label="Preparation time in minutes"
                  name="preparation_time"
                  placeholder=".30"
                  requirement="*"
                  />


        <TextInput handleChange={handleChange}
                  label="Total Duration"
                  name="duration"
                  placeholder="10"
                  requirement="*"
                  />



        <div style={{width:"50px"}}>  </div>

        {/*
        <TextInput handleChange={handleChange}
                  label="Answer recording time in minutes"
                  name="recording_time"
                  placeholder="1"
                  requirement="*"
                  />
       */}


        <TextAreaInputMedium  handleChange={handleChange}
                  label="Instructions"
                  name="instructionText"
                  placeholder="e.g. You will hear a conversation between a woman and a man. The man is a bus driver and the woman is a passenger trying to get somewhere."
                  requirement="*"
                  />



        { selectedAudioFile === null &&
             <div className={classes.ImageSubmissionStatus}> Audio/Video file will not be added </div>
        }

        { selectedAudioFile !== null &&
             <div className={classes.ImageSubmissionStatus}> Audio/Video file will  be added </div>
        }


        <div className={classes.audioFileDiv}>
             <input
                 type="file"
                 onChange={handleChangeAudioFile}
                 name="audioFile"
                 />

             <button type="button" onClick={removeAudioFileHandler}> Remove Audio/Video file </button>

        </div>


        { selectedFile === null &&
        <div className={classes.ImageSubmissionStatus}> Image will not be added </div>
	}

	{ selectedFile !== null &&
        <div className={classes.ImageSubmissionStatus}> Image will  be added </div>
	}	


	<div className={classes.audioFileDiv}>

          <input
              type="file"
              onChange={handleChangeImage}
              name="instructionImage"
              accept="image/*"
           />
           <button type="button" onClick={removeImageHandler}> Remove Image </button>

        </div>



        { selectedFile &&
            <img alt="not fount" width={"250px"} src={URL.createObjectURL(selectedFile.image[0])} className={classes.paraImageView} />
        }








        <div className={classes.submitButtonDiv}>
             <button type="submit"  className= {classes.submit_button} ><b> Add</b> </button>
        </div>


       </form>

    </div>


}


export default AddListeningTaskForm;
