import {useState} from 'react';

import classes from './ParagraphSections.module.css';
import {BsInfoCircle} from 'react-icons/bs';

import {GrRadialSelected} from 'react-icons/gr';






const OptionButton=(props)=>{







      return <button type="button"
                      className={classes.oneSelectListeningOptionButton}

                      onClick={props.selectOptionHandler}
                      >
                         <GrRadialSelected style={props.optionColor} />
                         <span style={{marginLeft:"20px"}}>{props.option.answerText}</span>
                      </button>


}

export default OptionButton;
