import {useState, useEffect} from 'react';
import classes from './ParagraphSections.module.css';
import {BsCaretDownFill, BsFillCircleFill} from 'react-icons/bs';

import OneOption from './OneOption';


import {deleteaquestion} from '../CommonApps/AllAPICalls';


import OutsideAlerter from '../CommonApps/OutsideAlerter';



const OneQuestion=(props)=>{



    const [showDropDown, setShowDropDown] = useState(false);

    const [selectedId, setSelectedId] = useState(null);
    const [selectedText, setSelectedText] = useState(null);

    let InActiveColor={color:"grey", marginRight:"10px",fontWeight:"normal"};
    let activeColor = {color:"var(--themeColor)", marginRight:"10px",fontWeight:"bold"};

    const showDropDownHandler=()=>{

       setShowDropDown(showDropDown=>!showDropDown);

    }

    const selectOptionHandler=(option)=>{
      setSelectedId(selectedId=>option.id);
      setSelectedText(selectedText=>option.answerText);
      //console.log("questionId: ", props.question.id);
      //console.log("optionId: ", option.id);
      //console.log("paragraphId: ", props.paragraphId);
      let one_Question = {"questionId": props.question.id,"answerId": option.id}

      props.pushSelectedAnswer(one_Question); 	    

     setShowDropDown(false);	    

    }

    useEffect(()=>{


    //console.log("new Paragraph");
    setSelectedId(selectedId=>null);
    setSelectedText(selectedText=>null);

    },[props.paragraphId]);


    //console.log("question: ", props.question);
    //console.log("selectedId: ", selectedId, "selectedText: ", selectedText);


    return   <div className={classes.oneQuestion}>
                              <i className={classes.oneQuestionText}> {props.question.questionNum+". "}{props.question.questionText} </i>

                              <div className={classes.dropDownOptionButtonDiv}>  

                                   <button type="button" className={classes.oneQuestionOptionsButton} onClick={showDropDownHandler}>
                                            { selectedId ===null &&
		                            <BsCaretDownFill /> 
                                            }
	                                    { selectedId !==null &&

                                              <b> {selectedText} </b>
					    }

		                   </button>


                                   { showDropDown &&

                                   <OutsideAlerter setDropDown={setShowDropDown}>
			               <div className={classes.dropdownAnswerOptions}>


                                        { props.question.anoptions.map((option, index)=>{

					      let optionId = option.id;

                                               return <OneOption key={index} 
						                 option={option} 
						                 selectOptionHandler={()=>selectOptionHandler(option)}
						                 optionColor={ selectedId === optionId ? activeColor: InActiveColor }
						                 oneExamData={props.oneExamData}
							         />

				           })

				        }

                                       </div>

				      </OutsideAlerter>	   

                                    }
                              </div>


              </div>


}

export default OneQuestion;
