//import {FiExternalLink} from 'react-icons/fi';
import {deleteexam} from '../../../../../../CommonApps/AllAPICalls';

import {MdDoubleArrow} from 'react-icons/md';



const OneExamShortView = (props)=>{



    let history = useHistory();
