import {useState, useEffect} from 'react';
import classes from './ParagraphSections.module.css';

import AddQuestionSection from './Forms/AddQuestionSection';

import {getallparagraphquestions, deleteaparagraph} from '../../../../../../CommonApps/AllAPICalls';

import OneQuestion from './OneQuestion';
import OneQuestionGrouped from './OneQuestionGrouped';
import {BsCaretDownFill} from 'react-icons/bs';

import {HiOutlineSwitchVertical} from 'react-icons/hi';


import GroupQuestionsForm from './Forms/GroupQuestionsForm';


const OneParagraphSection=(props)=>{



    const [showQuestionAddForm, setShowQuestionAddForm] = useState(false);

    //onClick={addQuestionHandler(paragraphId)

    const addQuestionHandler=(paragraphId)=>{
      setShowQuestionAddForm(true);
    }

    const [reloadQuestions, setReloadQuestions] = useState(false);

    const closeFormHandler=()=>{
      setShowQuestionAddForm(false);
      setReloadQuestions(reloadQuestions=>!reloadQuestions);
    }

    const [questionsData, getQuestionsData] = useState(null);

    useEffect(()=>{

       let paragraphId = props.oneParagraph.id;	   
       getallparagraphquestions({paragraphId, getQuestionsData});

    },[props.oneParagraph, reloadQuestions]);



   console.log("questionsData: ", questionsData);

   const deleteParagraphHandler=()=>{
       let paragraphId=props.oneParagraph.id;
       deleteaparagraph({paragraphId, props});
   }


   const reloadHandler=()=>{

    setReloadQuestions(reloadQuestions=>!reloadQuestions);

   }


   const [showGroupQuestionsForm, setShowGroupQuestionsForm] = useState(false);

   const groupQuestionsHandler=()=>{

     setShowGroupQuestionsForm(true);

   }	


   const closeGroupQuestionFormHandler=()=>{

   setShowGroupQuestionsForm(false);
   setReloadQuestions(reloadQuestions=>!reloadQuestions);
   }



   console.log("one Paragraph: ", props.oneParagraph);







return (


<div  className={classes.oneParaSection}>


                { showQuestionAddForm &&

                   <AddQuestionSection onPress = {closeFormHandler}
                                 selectedParagraphId={props.oneParagraph.id}
                               />

                }


                { showGroupQuestionsForm &&


                   <GroupQuestionsForm onPress={closeGroupQuestionFormHandler}
			               questionsData={questionsData}
			               paragraphId = {props.oneParagraph.id}
				       />



		}




                  <div className={classes.paraTitle}>
                        <span> <b>{props.oneParagraph.title} </b></span>
	                <button type="button" onClick={deleteParagraphHandler} style={{cursor:"pointer"}}> Delete </button>
                        <span> Time : {props.oneParagraph.duration} mins </span>
                  </div>
                  <div className={classes.content}>
                        <div className={classes.paragraphTextSec}>

                            { props.oneParagraph.contentType==="image" &&
	                    <img className={classes.userImage} src={props.oneParagraph.paragraphFile} alt='edr Logo' />
			    }

	                    { props.oneParagraph.contentType==="text" &&
                            <div className={classes.paragraphTextSec_margin}>  {props.oneParagraph.paragraphText} </div>
                            }


                        </div>



                        <div className={classes.paragraphQuestionSec}>

                           <button type="button" className={classes.addQuestionButton} onClick={addQuestionHandler}> +Add a question </button>

                           <button type="button" className={classes.addQuestionButton} onClick={groupQuestionsHandler}> group questions in one paragraph </button>


                           <div className={classes.allQuestionsOneSec}>



                            { questionsData !==null && questionsData.map((question, index)=>{

			          if(question.grouped==="yes"){
                                      return undefined;

			          }	   

			          if(question.grouped==="no"){ 	    
                                          return <OneQuestion key={index}
				                 question={question}
				                 reload={reloadHandler}
					         />
			          }        



			         })

			    }


	                </div>

                        <button type="button" className={classes.updownSwitch} onClick={flipSectionHandler}> <HiOutlineSwitchVertical/>  </button>

                        <div className={classes.allQuestionsOneSec} style={{marginTop:"20px",padding:"10px"}}>

                          { questionsData !==null && questionsData.map((question, index)=>{



                              if(question.grouped==="no"){
                                      return undefined;

                                  } 

                                  if(question.grouped==="yes"){
                                          return <OneQuestionGrouped key={index}
                                                 question={question}
                                                 reload={reloadHandler}
                                                 />
                                  }

			      })

	                  }

                        </div>


                        </div>
                  </div>
                  </div>

);






}

export default OneParagraphSection;
