import React,{useRef,useEffect, useState} from 'react';
import classes from './Exams.module.css'

import ExamsContainer from './ExamsContainer';


import ExamCreateForm from './Forms/CreateExamForm';

import {getallexams} from '../../../../../../CommonApps/AllAPICalls';



const Exams=(props)=>{

  const isMounted = useRef(false);
  useEffect(() => {
    isMounted.current = true;
    props.passMountInfo(true);
    return () => {
            isMounted.current = false
            props.passMountInfo(false);
    }
   }, [props]);


   const [rerender, setRerender] =useState(false);


   const [examsData, getExamsData] = useState(null);


   useEffect(()=>{


   getallexams({getExamsData}); 



   },[rerender]);



   const [showCreateExamForm, setShowCreateExamForm] =useState(false);


  const closeFormRerender=()=>{
  setShowCreateExamForm(false);	  
  setRerender(rerender=>!rerender);

  }	


  const rerenderHandler=()=>{

  setRerender(rerender=>!rerender);

  }



   console.log("examsData: ", examsData);



return (

<div className={classes.exams}>

   <div className={classes.switchBar}>

      <button className={classes.createCourseButton} type="button" onClick={()=>setShowCreateExamForm(true)}>
        <b> +Create an exam</b>
      </button>

    </div>


    { 
      showCreateExamForm && <ExamCreateForm  rerender={closeFormRerender}
	                                     onPress={()=>setShowCreateExamForm(false)}
	                                     />
    }






    <ExamsContainer examsData={examsData}
	             rerender={rerenderHandler}
	            />





</div>

);

}

export default Exams;
