import {useState, useEffect} from 'react';
import classes from './ExamResponses.module.css';


import AddResponsePlaceHolder from './Forms/AddResponsePlaceHolder';

import {getexamresponses} from '../../../../../../CommonApps/AllAPICalls';


import OneResponseShort from './OneResponseShort';


import OneDetailResponse from './OneDetailResponse';


const ExamResponses=(props)=>{



   const [selectedResponseId, setSelectedResponseId] = useState(null);

    const [responseData, setResponseData] = useState(null);

    const [rerender, setRerender] = useState(false);

    const [showAddResponseForm, setShowAddResponseForm] = useState(false);

    const showAddResponseFormHandler=()=>{
      setShowAddResponseForm(true);
    }

    const closeFormHandler=()=>{
      setShowAddResponseForm(false);   	  
    }


    useEffect(()=>{
     let examId = props.oneExamData.id;
     getexamresponses({examId, setResponseData}); 

    },[showAddResponseForm, rerender]);


    console.log("One Exam response: ", responseData);


    const rerenderHandler=()=>{

       setRerender(rerender=>!rerender);

    }

    const selectResponseHandler=(responseId)=>{

       console.log("responseId: ", responseId);	    
       setSelectedResponseId(selectedResponseId=>responseId);

    }

    const unSelectHandler=()=>{

	setSelectedResponseId(selectedResponseId=>null);    

    }





return <div className={classes.examResponse}>

    <button type="button" className={classes.responsePlaceHolder} onClick={showAddResponseFormHandler}> +Create a place holder</button>

    { showAddResponseForm &&
       <AddResponsePlaceHolder oneExamData={props.oneExamData}
	                     onPress={closeFormHandler}	                     />
    }


    { selectedResponseId === null && responseData !==null && responseData.map((OneResponse, index)=>{

         return <OneResponseShort key={index} 
	                          OneResponse={OneResponse} 
	                          rerender={rerenderHandler}
	                          examId = {props.oneExamData.id}
	                          selectResponse = {selectResponseHandler}
		                  />

         })	   
    }


    { selectedResponseId !==null &&

        <OneDetailResponse responseId = {selectedResponseId}
	                   unSelect = {unSelectHandler}
		           /> 

    }		    







</div>

}


export default ExamResponses;
