import {useEffect, useRef, useState} from 'react';
import classes from "./Header.module.css";
import {FaReact} from 'react-icons/fa';
import DropDownOne from './DropDownOne';
import {BsChevronDown} from 'react-icons/bs';
import Logo from "./logo.png";
import BimalenduName from "./BimalenduName.png";

import HeaderAddressBar from './HeaderAddressBar';


const  Header=(props)=> {

   const [showDropDownOne, setShowDropDownOne] = useState(false);
   const showDropDownOneHandler=()=>{
     setShowDropDownOne(true);
   }



   let activeColor="black";
   let inActiveColor="var(--themeColor)";



   const [buttonStyle, setButtonStyle] = useState({color:"black"});	


   useEffect(()=>{

     props.homeMounted && setButtonStyle({color:"var(--themeColor)"});
     !props.homeMounted && setButtonStyle({color:"black"});


   },[props.homeMounted]);


   useEffect(()=>{

     props.aboutMounted && setButtonStyle({color:"var(--themeColor)"});
     !props.aboutMounted && setButtonStyle({color:"black"});


   },[props.aboutMounted]);









   console.log("props.homeMounted", props.homeMounted);






    return (
    <div className={classes.parentFooter}>

        <HeaderAddressBar/>

        {/* top Navigation bar */}
        <div className={classes.topNavigationBar}>
          <div className={classes.nFirstCotainer}>
            {/* <div className={classes.logo}>  */}
	       <img className={classes.logo} src={Logo} />

         <div className={classes.Name}>Bimalendu Pradhan</div>


	    {/* </div> */}
            {/*

	      <img src={BimalenduName}/>
	    </div>
	    */}
          </div>

          <div className={classes.navigationMenu}>

            <div className={classes.headerButtonDiv}>
              <button type="button" className={classes.headerButton} onClick={props.homeHandler} style={buttonStyle}>
                <div className={classes.HomeText}>Home</div>
              </button>
            </div>

            <div className={classes.headerButtonDiv}>
              <button type="button" className={classes.headerButton} onClick={props.aboutHandler}>
                <div className={classes.headerButtonText}>About</div>
              </button>
            </div>


	    <div className={classes.headerButtonDiv}>
              <button type="button" className={classes.headerButton} onClick={props.newsHandler}>
                <div className={classes.headerButtonText}>News</div>
              </button>
            </div>



	    <div className={classes.headerButtonDiv}>
              <button type="button" className={classes.headerButton} onClick={showDropDownOneHandler}>
                <div className={classes.headerButtonText}><span>Home Buyer</span> <BsChevronDown size={15} style={{marginLeft:"5px"}}/></div>
              </button>
                  { showDropDownOne && 
	                         <DropDownOne setShowDropDownOne={setShowDropDownOne} /> 
		  }
            </div>


            <div className={classes.headerButtonDiv}>
              <button type="button" className={classes.headerButton} onClick={props.contactUsHandler}>
                <div className={classes.headerButtonText}>Contact us</div>
              </button>
            </div>

          </div>
        </div>

    </div>


    );
}

export default Header;
