import {useState} from 'react';
import classes from './CreateExamForm.module.css';
import {AiFillCloseCircle} from "react-icons/ai";

import {OptionField, TextInput} from '../../../../../../../CommonApps/FormInputObjects';

import {createcelpipexam} from '../../../../../../../CommonApps/AllAPICalls';


const CreateExamForm=(props)=>{



   const initialFormData = Object.freeze({
        name: "",
        institutename: "",
        difficultylevel: "" 
        });

   const [formData, updateFormData] = useState(initialFormData);












   const handleSubmit=(e)=>{

   e.preventDefault();	   
   if(formData.name===""){
            alert('please enter a name ');
                        return null;
          }

   if(formData.institutename===""){
            alert('please enter institute name ');
                        return null;
          }

   if(formData.difficultylevel===""){
            alert('please choose a level');
                        return null;
          }



   createcelpipexam({formData, props});


   }



   const handleChange=(e)=>{

    updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value.trim(),
                });

    }



    console.log("forData: ", formData);



   let intervals= [{"id": "easy","name": "easy"},
                       {"id": "medium","name": "medium"},
                       {"id": "difficult","name": "difficult"},
                      ];












return (

 <div className={classes.createExamFormParent}>


   <form className={classes.createExamForm} onSubmit={handleSubmit}>


   <div className={classes.closeButtonDiv}>
                 <button onClick={props.onPress}
                       className={classes.closeFormButton}>
                            <AiFillCloseCircle className={classes.closeButtonIcon}/>
                 </button>
          </div>





    <div className={classes.inputBoxesDiv}>

       <TextInput handleChange={handleChange} 
	          label="Exam name" 
	          name="name" 
	          placeholder=" CELPIP Practice Test - 1 "
	          requirement="*"
	          />

       <TextInput handleChange={handleChange} 
	          label="Institute name" 
	          name="institutename" 
	          placeholder="e.g NewCrown English Classes "
	          requirement="*"
	          />

        <OptionField handleChange={handleChange}
                             label="Difficulty level"
                             name="difficultylevel"
                             options={intervals}
	                     requirement="*"
                             />



             <div className={classes.submitButtonDiv}>
                <button type="submit"  className= {classes.submit_button} ><b>Create </b> </button>
             </div>


    </div>	

  </form>


 </div>

);


}


export default CreateExamForm;
