import {useState} from 'react';
import classes from './WritingTest.module.css';


import OneWritingTask from './OneWritingTask';
import InfoHeader from './InfoHeader';


const WritingTest=(props)=>{



    const [paraSecNo, setParaSecNo] = useState(0);

    const [OneParagraph, setOneParagraph] = useState(
            props.oneExamData.writingTest !==null  && props.oneExamData.writingTest.writingSections.length > 0 ?
             props.oneExamData.writingTest.writingSections[paraSecNo] : null
    );



   const nextSecHandler=()=>{

       let paraNextNum=Number(paraSecNo+1);

        if(props.oneExamData.writingTest !==null  &&
                props.oneExamData.writingTest.writingSections.length > 0 &&
                paraNextNum < Number(props.oneExamData.writingTest.writingSections.length)
          ){

           setOneParagraph(OneParagraph=> props.oneExamData.writingTest.writingSections[paraSecNo+1]);
           setParaSecNo(paraSecNo=>paraSecNo+1);
        }


        if(props.oneExamData.writingTest !==null  &&
                props.oneExamData.writingTest.writingSections.length > 0 &&
                 paraNextNum >= Number(props.oneExamData.writingTest.writingSections.length)){

                props.next();

        }







   }




    return  <>  
		<InfoHeader info="You are taking reading test now!"/>
	      { OneParagraph !==null &&	<OneWritingTask  OneTask={OneParagraph} 
	                         rerender={props.rerender} 
	                         nextSecHandler={nextSecHandler}
	                         responseId={props.responseId}
		                 />

              }

              { OneParagraph ===null &&
              <>
              <div style={{marginLeft:"50px"}}> No material in this section </div>
              <button type="button" onClick={nextSecHandler} style={{ color:"white", backgroundColor:"blue",marginLeft:"50px",marginTop:"10px",cursor:"pointer"}}> Move to Next </button>
             </>
           }








	    </>	


}


export default WritingTest
