import {useState} from 'react';
import classes from "./Website.module.css";
import { Route, Switch, useHistory } from "react-router-dom";
import Header from "./Header";
import Footer from "./Footer";
import Home from "./Home/Home";
import About from "./About/About";
import News from "./News/News";
import HomeBuyer from "./ClientPage1/HomeBuyer";
import ContactUs from "./Contactus/ContactUs";


const Website = () => {
   const registerHandler = () => {
     window.open("createaccount", "_blank");
   };

   const loginHandler = () => {
     window.open("app/dashboard/general/exams", "_blank");
   };

   const [showHome, setShowHome] = useState(true);
   const [showAbout, setShowAbout] = useState(false);
   const [showNews, setShowNews] = useState(false);
   const [showHomeBuyer, setShowHomeBuyer] = useState(false);
   const [showContactUs, setShowContactUs] = useState(false);



   const [homeMounted, setHomeMounted] = useState(false);
   const [aboutMounted, setAboutMounted] = useState(false);
   const [newsMounted, setNewsMounted] = useState(false);
   const [homeBuyerMounted, setHomeBuyerMounted] = useState(false);
   const [contactUsMounted, setContactUsMounted] = useState(false);



   const homeHandler=()=>{
     setShowHome(true);
     setShowAbout(false);
     setShowNews(false);
     setShowHomeBuyer(false);
     setShowContactUs(false);

   }

  const aboutHandler=()=>{
     setShowHome(false);
     setShowAbout(true);
     setShowNews(false);
     setShowHomeBuyer(false);
     setShowContactUs(false);

  }

  const newsHandler =()=>{
     setShowHome(false);
     setShowAbout(false);
     setShowNews(true);
     setShowHomeBuyer(false);
     setShowContactUs(false);

  }

  const homeBuyerHandler=()=>{
     setShowHome(false);
     setShowAbout(false);
     setShowNews(false);
     setShowHomeBuyer(true);
     setShowContactUs(false);

  }

  const contactUsHandler=()=>{
     setShowHome(false);
     setShowAbout(false);
     setShowNews(false);
     setShowHomeBuyer(false);
     setShowContactUs(true);

  }







  return (
    <div className={classes.website}>
      <div className={classes.innerDiv}>
        <Header homeHandler={homeHandler}
	        aboutHandler ={aboutHandler}
	        newsHandler = {newsHandler}
	        homeBuyerHandler={homeBuyerHandler}
	        contactUsHandler ={contactUsHandler}

	  />
	  { showHome && 
		   <Route exact path='/' >
		       <Home/>
                   </Route>
	  }
	  { showAbout && 
			<Route exact path='/about' >  
			  <About/>
			</Route>
	  }
	  { showNews && 
			 <Route exact path='/news'> 
			    <News/>
                         </Route>
	  }
	  { showHomeBuyer && 
			  <Route exact path='/homebuyer'>
			    <HomeBuyer/>
                          </Route>
	  }
	  { showContactUs && 
			  <Route exact path='/contactus'>
			     <ContactUs/>
                          </Route>
	  }

        <Footer />
      </div>
    </div>
  );
};

export default Website;
