import {useState} from 'react';
import classes from './ParagraphSections.module.css';



import OneListeningTask from './OneListeningTask';


import AddListeningTaskForm from './Forms/AddListeningTaskForm';

const ListeningTest=(props)=>{


    //  console.log("one exam: ", props.oneExamData);


   const [showListeningTaskAddForm, setShowListeningTaskAddForm] = useState(false);



   const showListeningAddFormHandler=()=>{

      setShowListeningTaskAddForm(true);    	   

   }


   const closeFormHandler=()=>{

    setShowListeningTaskAddForm(false);
    props.rerender();
   }



  //return <OneListeningTask key={index} oneParagraph={OneTask}
  //                                                          rerender={props.rerender}
  //                                                          />



   return (

      <div className={classes.paragraphSections} >


      <button type="button" className={classes.createButton} onClick={showListeningAddFormHandler}> + Add a listening task  </button> 


       { showListeningTaskAddForm && 

         <AddListeningTaskForm oneExamData={props.oneExamData} onPress={closeFormHandler}/>

       }







         { props.oneExamData !==null && 
	            props.oneExamData.listeningTest !==null && 
	            props.oneExamData.listeningTest.listeningSections.map((OneTask,index)=>{

                    return <OneListeningTask  key ={index} OneTask={OneTask} rerender={closeFormHandler}/>


             })

         }



  </div>

);


}

export default ListeningTest;
