import {useState,useEffect} from 'react';
import {Route,Switch, useHistory} from 'react-router-dom';


import classes from './TestSite.module.css';

import { TextInput } from './../CommonApps/FormInputObjects';

import {verifycandidatefortestlogin} from './../CommonApps/AllAPICalls';

import TestHomePage from './TestHomePage';


const TestSite=()=>{



    const initialFormData = Object.freeze({
        name: "",
        password: "",
        });

    const [formData, updateFormData] = useState(initialFormData);


    const [testId,setTestId] = useState(null);

    const [responseId, setResponseId] = useState(null);

    const [testURL, setTestURL] = useState(null);

    const [testPath, setTestPath]= useState(null);


    const [authenticated, setAuthenticated] = useState(false);	


    useEffect(()=>{

     let testURL=window.location.href;
     let pattern = '/takethistest/[1-9]+/[1-9]+/';
     let result = testURL.match(pattern);

     console.log("result: ", result);

     if (result) {
       let urlarray=testURL.split("/");

       console.log("urlarray: ", urlarray);	     

       let response_Id = urlarray[urlarray.length-2];
       let test_Id = urlarray[urlarray.length-3];

       console.log("--------------",urlarray[urlarray.length-1])	     

       setTestId(test_Id);
       setResponseId(response_Id);
       setTestPath(result+"/");
     }

    },[]);







   const handleChange=(e)=>{
    updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value,
                });

   }



   const handleSubmit=(e)=>{
    e.preventDefault();

     if(formData.name === "" ){
            alert('please enter your name');
                        return null;
          }

     if(formData.password === "" ){
            alert('please enter password');
                        return null;
          }

     let examId=testId;

     let password=formData.password;	
     let name = formData.name;	   
     if(testId !==null && responseId !==null && password !==null){
	  console.log(examId,"--", responseId,"--", password,"--", name);   
          verifycandidatefortestlogin({examId, responseId, password,name, setAuthenticated});	   
     }

   }





  console.log("formData: ",formData );



return <div className={classes.testSite}>


       { !authenticated && <>		
         <div className={classes.homePageMessage}> 
		<h2>Welcome!! Are you ready to take the Test?</h2> 
         </div>

         <div className={classes.loginInfo}>
	     <form  onSubmit={handleSubmit}>  
             <TextInput handleChange={handleChange}
                  label="Full Name"
                  name="name"
                  placeholder="e.g. John Stupak"
                  requirement="*"
                  />

              <TextInput handleChange={handleChange}
                  label="Test Unique password"
                  name="password"
                  placeholder="e.g. 123426"
                  requirement="*"
                  />


             <button type="buttom" className={classes.submitButton}> Submit </button>


	     </form> 
         </div>
        </>
       }



      { authenticated && 
      <Switch>
           <Route exact path={testPath} >
                   <TestHomePage examId={testId} responseId={responseId}/>
           </Route>
      </Switch>
      }






</div>


}

export default TestSite;
