import {useState} from 'react';

import InfoHeader from './InfoHeader';

import OneSpeakingTask from './OneSpeakingTask';


const SpeakingTest=(props)=>{

    const [paraSecNo, setParaSecNo] = useState(0);

    const [OneParagraph, setOneParagraph] = useState(
            props.oneExamData.speakingTest !==null  && props.oneExamData.speakingTest.speakingSections.length > 0 ?
             props.oneExamData.speakingTest.speakingSections[paraSecNo] : null
    );




   const nextSecHandler=()=>{


        let paraNextNum=Number(paraSecNo+1);

        if(props.oneExamData.speakingTest !==null  &&
                props.oneExamData.speakingTest.speakingSections.length > 0 &&
                paraNextNum < Number(props.oneExamData.speakingTest.speakingSections.length)
          ){

           setOneParagraph(OneParagraph=> props.oneExamData.speakingTest.speakingSections[paraSecNo+1]);
           setParaSecNo(paraSecNo=>paraSecNo+1);
        }


        if(props.oneExamData.speakingTest !==null  &&
                 props.oneExamData.speakingTest.speakingSections.length > 0 &&
                 paraNextNum >= Number(props.oneExamData.speakingTest.speakingSections.length)){

                props.next();

        }





   }






  return   <>
		<InfoHeader info="You are taking speaking test now!"/>
		<OneSpeakingTask  OneTask={OneParagraph} 
	                          nextSecHandler={nextSecHandler}
	                          responseId={props.responseId}
		                  />

	   </>	

}


export default SpeakingTest
