import {useState} from 'react';
import classes from './WritingTest.module.css';

import {TextAreaInput} from '../CommonApps/FormInputObjects';
import {addwritingtaskresponse} from '../CommonApps/AllAPICalls';

import InfoHeader from './InfoHeader';

import Countdown from 'react-countdown';

const OneWritingTask=(props)=>{


  const [wordCount, setWordCount] = useState(0);

  const initialFormData = Object.freeze({
        paragraphText: "",
        });

  const [formData, updateFormData] = useState(initialFormData);


  const handleChange=(e)=>{



      updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value,
                });

     setWordCount(wordCount=>formData.paragraphText.split(" ").length);

    }

    console.log("formData: ", formData);


     const renderer = ({ hours, minutes, seconds, completed }) => {
      if (completed) {
       // Render a completed state

         return <div> Over </div>

       } else {
       // Render a countdown
        if (minutes ===0){
              return <span style={{background:"red", color:"white",paddingLeft:"10px", paddingRight:"10px"}}> {minutes}:{seconds} mins</span>;
         } else{

              return <span> {minutes}:{seconds} mins</span>;
         }
       }
     };





    let reTime = Number(props.OneTask.duration)*60*1000;
    console.log("writing duration: ", props.OneTask.duration)
    console.log("Date.now(): ", Date.now());

    let timePrep = Date.now()+reTime ;


    const nextSecHandler =()=>{

      let writingTaskId = props.OneTask.id;
      let responseId = props.responseId;
      let paragraphText = formData.paragraphText; 

      if (paragraphText == ""){

	 alert("Enter some text in the box");
	 return null;

      }

      addwritingtaskresponse({writingTaskId, responseId, paragraphText, props});

      // props.nextSecHandler();


    }    


    console.log("paragraph: ", formData);




return      <div className={classes.oneWritingTask}>
              <div className={classes.writingTitle}>
                        <span> <b> {props.OneTask.title} </b></span>
                        <div>

                           <span>  Time  remaining:
                              <Countdown date={timePrep}
                             renderer={renderer}
                             onComplete={nextSecHandler}
                             key={props.OneTask.id}
                            />

                           </span>

                           <button type="button" className={classes.nextSectionButton} onClick={nextSecHandler}> NEXT </button>
                        </div>

              </div>


              <div className={classes.textQuestionBoxes}>

		  <div className={classes.textQuestionBoxLeft}> 
		     <div style={{color:"var(--themeColor)",marginBottom:"20px"}}><b> Read the following information</b></div> 

		     {props.OneTask.questionText1}
		  </div>


                  <div className={classes.textQuestionBoxRight}> 

		  <div className={classes.questionTextArea2}>

		     {props.OneTask.questionText2}
                  </div>

                  <div style={{marginLeft:"20px"}}>
                  Word Count: <span style={{color:"red"}}>{wordCount+1}</span>
                  </div>
		 <textarea
	            name="paragraphText"
                    type="text"
	            onChange={handleChange}
                    className={classes.input_field_textarea}
                    style={{fontSize: "16px",padding:"20px"}}
	            placeholder="Dear Sir .."
                   />

		  </div>

              </div>


    </div>



}


export default OneWritingTask;
